package com.wwj.likoute;

import org.junit.Test;

/**
 * @author AleckWei
 * @detail 给定字符串 s 和 t ，判断 s 是否为 t 的子序列。
 * 字符串的一个子序列是原始字符串删除一些（也可以不删除）字符而不改变剩余字符相对位置形成的新字符串。
 * （例如，"ace"是"abcde"的一个子序列，而"aec"不是）。
 */
public class IsSubStrTest {

    /*
        示例 1：
        输入：s = "abc", t = "ahbgdc"
        输出：true
     */

    @Test
    public void fun() {
//        String s = "abc", t = "ahbgdc";
//        String s = "axc", t = "ahbgdc";

        String s = "leeeeetcode";
        String t = "";
        boolean res = isSubsequence(s, t);

        System.out.println(res);
    }

    public boolean isSubsequence(String s, String t) {
        if ("".equals(s)) {
            // 当s为空时，t不管是什么都算有s了
            return true;
        }

        boolean res = true;
        String[] splitStrArray = s.split("");
        for (String singleStr : splitStrArray) {
            if (!t.contains(singleStr)) {
                res = false;
                break;
            }

            int firstIndexOfSingleChar = t.indexOf(singleStr);
            if (firstIndexOfSingleChar > -1) {
                // 证明该t字符串中有这个字符
                // 然后从这个地方截取后面的，保证后续都是从后面开始找
                t = t.substring(firstIndexOfSingleChar + 1);
            }
        }

        return res;
    }

}
